function ApplicationOption_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobileworkarea_10': 'ApplicationOption_mobileworkarea_10',
        'mobilegrid_24': 'ApplicationOption_mobilegrid_24',
        'mobilegridcell_25': 'ApplicationOption_mobilegridcell_25',
        'mobilebutton_back': 'ApplicationOption_mobilebutton_back',
        'mobilegridcell_26': 'ApplicationOption_mobilegridcell_26',
        'mobilegridcell_29': 'ApplicationOption_mobilegridcell_29',
        'mobilebutton_next': 'ApplicationOption_mobilebutton_next',
        'mobilelabel_11': 'ApplicationOption_mobilelabel_11',
        'mobilegrid_17': 'ApplicationOption_mobilegrid_17',
        'mobilegridcell_18': 'ApplicationOption_mobilegridcell_18',
        'mobilegridcell_19': 'ApplicationOption_mobilegridcell_19',
        'mobileradiogroup_driver_option': 'ApplicationOption_mobileradiogroup_driver_option',
        'mobileradiobutton_yes': 'ApplicationOption_mobileradiobutton_yes',
        'mobileradiobutton_no': 'ApplicationOption_mobileradiobutton_no',
        'mobilegridcell_22': 'ApplicationOption_mobilegridcell_22',
        'mobilenavbar_6': 'ApplicationOption_mobilenavbar_6',
        'favorite_item': 'ApplicationOption_favorite_item',
        'home_item': 'ApplicationOption_home_item',
        'upload_item': 'ApplicationOption_upload_item'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'ApplicationOption';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var ApplicationOption_onLoad = function() {
            ApplicationOption_elementsExtraJS();

            ApplicationOption_deviceEvents();
            ApplicationOption_windowEvents();
            ApplicationOption_elementsEvents();
        };

    // screen window events


    function ApplicationOption_windowEvents() {

        $('#ApplicationOption').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function ApplicationOption_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function ApplicationOption_elementsExtraJS() {
        // screen (ApplicationOption) extra code

    };

    // screen elements handler


    function ApplicationOption_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#ApplicationOption_mobilecontainer1 [name="mobilebutton_next"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    user_info_data.delivery_driver_job = Appery("mobileradiogroup_driver_option").find(":checked").val();
                    Apperyio.storage.user_info.set(user_info_data);
                    Apperyio.navigateTo('ChooseUserName', {
                        reverse: false
                    });

                }
            },
        }, '#ApplicationOption_mobilecontainer1 [name="mobilebutton_next"]');

        $(document).off("click", '#ApplicationOption_mobilefooter1 [name="favorite_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('Favorite', {
                        reverse: false
                    });

                }
            },
        }, '#ApplicationOption_mobilefooter1 [name="favorite_item"]');
        $(document).off("click", '#ApplicationOption_mobilefooter1 [name="home_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('Home', {
                        reverse: false
                    });

                }
            },
        }, '#ApplicationOption_mobilefooter1 [name="home_item"]');
        $(document).off("click", '#ApplicationOption_mobilefooter1 [name="upload_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('TypeChoice', {
                        reverse: false
                    });
                    localStorage.setItem('page_from', 'UploadAd');

                }
            },
        }, '#ApplicationOption_mobilefooter1 [name="upload_item"]');

    };

    $(document).off("pagebeforeshow", "#ApplicationOption").on("pagebeforeshow", "#ApplicationOption", function(event, ui) {
        Apperyio.CurrentScreen = "ApplicationOption";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    ApplicationOption_onLoad();
};

$(document).off("pagecreate", "#ApplicationOption").on("pagecreate", "#ApplicationOption", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    ApplicationOption_js();
});