    function Home_windowEvents() {

        $('#Home').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#Home').on({
            pageshow: function(event) { //search option
                saerchPara = {
                    'search_type': 'auto',
                    'search_key_json': 'null'
                }
                Apperyio.storage.searchPara.set(saerchPara);

                //Here you can set the needed "limit". Currently it's set to 12.
                var infiniteList = {
                    limit: "10",
                    skip: "0",
                    noMoreItems: undefined
                };
                Apperyio.storage.dogsInfinite.set(infiniteList);
                jQuery(window).scroll(onScrollHandler);
                var onScrollBottom = function() {

                        var infiniteList = Apperyio.storage.dogsInfinite.get();
                        if (infiniteList.noMoreItems == "true") return;

                        infiniteList.skip = parseInt(infiniteList.skip) + parseInt(infiniteList.limit) + "";

                        Apperyio.storage.dogsInfinite.set(infiniteList);
                        dogsListInfinite.execute(); //{async:false}

                    };

                jQuery(window).bind("onScrollBottom", onScrollBottom);

                ;
                try {
                    dogsListInfinite.execute({});
                } catch (e) {
                    console.error(e);
                    hideSpinner();
                };
            },
        });

    };

