function PhoneConfirm_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilelabel_2': 'PhoneConfirm_mobilelabel_2',
        'spacer_4': 'PhoneConfirm_spacer_4',
        'spacer_5': 'PhoneConfirm_spacer_5',
        'mobilelabel_phone_no': 'PhoneConfirm_mobilelabel_phone_no',
        'spacer_6': 'PhoneConfirm_spacer_6',
        'spacer_7': 'PhoneConfirm_spacer_7',
        'spacer_8': 'PhoneConfirm_spacer_8',
        'mobilelabel_9': 'PhoneConfirm_mobilelabel_9',
        'mobilegrid_10': 'PhoneConfirm_mobilegrid_10',
        'mobilegridcell_11': 'PhoneConfirm_mobilegridcell_11',
        'mobilebutton_edit': 'PhoneConfirm_mobilebutton_edit',
        'mobilegridcell_15': 'PhoneConfirm_mobilegridcell_15',
        'mobilebutton_yes': 'PhoneConfirm_mobilebutton_yes'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'PhoneConfirm';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var PhoneConfirm_onLoad = function() {
            PhoneConfirm_elementsExtraJS();

            PhoneConfirm_deviceEvents();
            PhoneConfirm_windowEvents();
            PhoneConfirm_elementsEvents();
        };

    // screen window events


    function PhoneConfirm_windowEvents() {

        $('#PhoneConfirm').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#PhoneConfirm').on({
            pageshow: function(event) {
                Appery("mobilelabel_phone_no").text(localStorage.getItem('phone_no'));
            },
        });

    };

    // device events


    function PhoneConfirm_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function PhoneConfirm_elementsExtraJS() {
        // screen (PhoneConfirm) extra code

    };

    // screen elements handler


    function PhoneConfirm_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#PhoneConfirm_mobilecontainer [name="mobilebutton_edit"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('ApproveActivatePhone', {
                        reverse: false
                    });

                }
            },
        }, '#PhoneConfirm_mobilecontainer [name="mobilebutton_edit"]');

        $(document).off("click", '#PhoneConfirm_mobilecontainer [name="mobilebutton_yes"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    var phone_no = Appery("mobilelabel_phone_no").text();
                    a = Math.floor((Math.random() * 9) + 1)
                    b = Math.floor((Math.random() * 9) + 1)
                    c = Math.floor((Math.random() * 9) + 1)
                    d = Math.floor((Math.random() * 9) + 1)
                    var activation_code = a + '' + b + '' + c + '' + d;
                    user_info_data = Apperyio.storage.user_info.get();
                    user_info_data = {
                        'phone_no': phone_no,
                        'activation_code': activation_code
                    }
                    Apperyio.storage.user_info.set(user_info_data);

                    ;
                    Apperyio.navigateTo('RecevingSMSCode', {
                        reverse: false
                    });

                }
            },
        }, '#PhoneConfirm_mobilecontainer [name="mobilebutton_yes"]');

    };

    $(document).off("pagebeforeshow", "#PhoneConfirm").on("pagebeforeshow", "#PhoneConfirm", function(event, ui) {
        Apperyio.CurrentScreen = "PhoneConfirm";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    PhoneConfirm_onLoad();
};

$(document).off("pagecreate", "#PhoneConfirm").on("pagecreate", "#PhoneConfirm", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    PhoneConfirm_js();
});