    // screen window events


    function Profile_windowEvents() {

        $('#Profile').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#Profile').on({
            pageshow: function(event) {
                user_info_data = Apperyio.storage.user_info.get();
                Apperyio("mobilelabel_username").text(user_info_data.username);
                Apperyio("mobilelabel_nickname").text(user_info_data.nick_name);
                try {
                    restservice_my_posted_ad.execute({});
                } catch (e) {
                    console.error(e);
                    hideSpinner();
                };
                try {
                    restservice_get_profile_image.execute({});
                } catch (e) {
                    console.error(e);
                    hideSpinner();
                };
            },
        });

    };

