function TypeChoice_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobileworkarea_10': 'TypeChoice_mobileworkarea_10',
        'mobilelabel_34': 'TypeChoice_mobilelabel_34',
        'mobilebutton_33': 'TypeChoice_mobilebutton_33',
        'mobilegrid_type': 'TypeChoice_mobilegrid_type',
        'mobilegridcell_21': 'TypeChoice_mobilegridcell_21',
        'mobilelist_30': 'TypeChoice_mobilelist_30',
        'mobilelistitem_type': 'TypeChoice_mobilelistitem_type',
        'mobilelistitembutton_32': 'TypeChoice_mobilelistitembutton_32',
        'mobilelabel_type': 'TypeChoice_mobilelabel_type',
        'mobilelabel_hidden_id': 'TypeChoice_mobilelabel_hidden_id',
        'mobilenavbar_6': 'TypeChoice_mobilenavbar_6',
        'favorite_item': 'TypeChoice_favorite_item',
        'home_item': 'TypeChoice_home_item',
        'upload_item': 'TypeChoice_upload_item'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.mappings["TypeChoice_restservice_type4_onbeforesend_mapping_0"] = {
        "homeScreen": "TypeChoice",
        "directions": []
    };

    Apperyio.mappings["TypeChoice_restservice_type4_onsuccess_mapping_0"] = {
        "homeScreen": "TypeChoice",
        "directions": [

        {
            "from_name": "restservice_type4",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "TypeChoice",
            "to_type": "UI",

            "mappings": [

            {

                "source": "$['body'][i]['id']",
                "target": "$['mobilegrid_type']['mobilelabel_hidden_id:text']"

            },

            {

                "source": "$['body'][i]['type_name']",
                "target": "$['mobilegrid_type']['mobilelabel_type:text']"

            },

            {

                "source": "$['body'][i]",
                "target": "$['mobilegrid_type']"

            }

            ]
        }

        ]
    };

    Apperyio.datasources = Apperyio.datasources || {};

    window.restservice_type4 = Apperyio.datasources.restservice_type4 = new Apperyio.DataSource(Type_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["TypeChoice_restservice_type4_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["TypeChoice_restservice_type4_onsuccess_mapping_0"]);
        },
        "onError": function(jqXHR, textStatus, errorThrown) {}
    });

    Apperyio.CurrentScreen = 'TypeChoice';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var TypeChoice_onLoad = function() {
            TypeChoice_elementsExtraJS();

            TypeChoice_deviceEvents();
            TypeChoice_windowEvents();
            TypeChoice_elementsEvents();
        };

    // screen window events


    function TypeChoice_windowEvents() {

        $('#TypeChoice').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#TypeChoice').on({
            pageshow: function(event) {
                try {
                    restservice_type4.execute({});
                } catch (e) {
                    console.error(e);
                    hideSpinner();
                };
            },
        });

    };

    // device events


    function TypeChoice_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function TypeChoice_elementsExtraJS() {
        // screen (TypeChoice) extra code

        /* mobilelist_30 */

        listView = $("#TypeChoice_mobilelist_30");
        theme = listView.attr("data-theme");
        if (typeof theme !== 'undefined') {
            var themeClass = "ui-btn-up-" + theme;
            listItem = $("#TypeChoice_mobilelist_30 .ui-li-static");
            $.each(listItem, function(index, value) {
                $(this).addClass(themeClass);
            });
        }

        /* mobilelistitem_type */

    };

    // screen elements handler


    function TypeChoice_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#TypeChoice_mobilecontainer1 [name="mobilebutton_33"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Home', {
                        reverse: false
                    });

                }
            },
        }, '#TypeChoice_mobilecontainer1 [name="mobilebutton_33"]');

        $(document).off("click", '#TypeChoice_mobilecontainer1 [name="mobilelistitem_type"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    _idx = $(this).attr('_idx');
                    disid = "mobilelabel_type" + _idx;
                    type_name = $("[dsid=" + disid + "]").html();

                    _idx = $(this).attr('_idx');
                    disid = "mobilelabel_hidden_id" + _idx;
                    id = $("[dsid=" + disid + "]").html();

                    dataset = {
                        'type_id': id,
                        'type_name': type_name
                    };
                    Apperyio.storage.typeChoice.set(dataset);

                    Appery.navigateTo(localStorage.getItem('page_from'));

                    ;

                }
            },
        }, '#TypeChoice_mobilecontainer1 [name="mobilelistitem_type"]');

        $(document).off("click", '#TypeChoice_mobilefooter1 [name="favorite_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('Favorite', {
                        reverse: false
                    });

                }
            },
        }, '#TypeChoice_mobilefooter1 [name="favorite_item"]');
        $(document).off("click", '#TypeChoice_mobilefooter1 [name="home_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('Home', {
                        reverse: false
                    });

                }
            },
        }, '#TypeChoice_mobilefooter1 [name="home_item"]');
        $(document).off("click", '#TypeChoice_mobilefooter1 [name="upload_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('TypeChoice', {
                        reverse: false
                    });
                    localStorage.setItem('page_from', 'UploadAd');

                }
            },
        }, '#TypeChoice_mobilefooter1 [name="upload_item"]');

    };

    $(document).off("pagebeforeshow", "#TypeChoice").on("pagebeforeshow", "#TypeChoice", function(event, ui) {
        Apperyio.CurrentScreen = "TypeChoice";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    TypeChoice_onLoad();
};

$(document).off("pagecreate", "#TypeChoice").on("pagecreate", "#TypeChoice", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    TypeChoice_js();
});