    // screen window events


    function UploadAd_windowEvents() {

        $('#UploadAd').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#UploadAd').on({
            pageshow: function(event) {
                try {
                    restservice_breed.execute({});
                } catch (e) {
                    console.error(e);
                    hideSpinner();
                };
                try {
                    restservice_contry.execute({});
                } catch (e) {
                    console.error(e);
                    hideSpinner();
                };
                ImageData.length = 0;
                VideoData.length = 0;
                $("#id_image_content").html('');
                Apperyio.processMappingAction(Apperyio.mappings["UploadAd_UploadAd_onpageshow_mapping_3"]);
            },
        });

    };

