function Welcome_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobileworkarea_10': 'Welcome_mobileworkarea_10',
        'mobilelabel_11': 'Welcome_mobilelabel_11',
        'mobilenavbar_6': 'Welcome_mobilenavbar_6',
        'favorite_item': 'Welcome_favorite_item',
        'home_item': 'Welcome_home_item',
        'upload_item': 'Welcome_upload_item'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'Welcome';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var Welcome_onLoad = function() {
            Welcome_elementsExtraJS();

            Welcome_deviceEvents();
            Welcome_windowEvents();
            Welcome_elementsEvents();
        };

    // screen window events


    function Welcome_windowEvents() {

        $('#Welcome').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#Welcome').on({
            pageshow: function(event) {
                user_info_data = Apperyio.storage.user_info.get();
                try {

                    phone_no = user_info_data.phone_no;
                } catch (err) {
                    Appery.navigateTo('ApplicationFirstPage', {});
                }

                if (typeof(user_info_data) === "undefined") {
                    Appery.navigateTo('ApplicationFirstPage', {});
                } else if (phone_no.length === 0) {
                    Appery.navigateTo('ApplicationFirstPage', {});
                } else {
                    Appery.navigateTo('Home', {});
                };
            },
        });

    };

    // device events


    function Welcome_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function Welcome_elementsExtraJS() {
        // screen (Welcome) extra code

    };

    // screen elements handler


    function Welcome_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#Welcome_mobilefooter1 [name="favorite_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('Favorite', {
                        reverse: false
                    });

                }
            },
        }, '#Welcome_mobilefooter1 [name="favorite_item"]');
        $(document).off("click", '#Welcome_mobilefooter1 [name="home_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('Home', {
                        reverse: false
                    });

                }
            },
        }, '#Welcome_mobilefooter1 [name="home_item"]');
        $(document).off("click", '#Welcome_mobilefooter1 [name="upload_item"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    user_info_data = Apperyio.storage.user_info.get();
                    if (typeof(user_info_data) === "undefined") {
                        Toast("You are not a registered user.Please make an account first");
                        return;
                    } else {
                        phone_no = user_info_data.phone_no;
                        if (phone_no.length === 0) {
                            Toast("You are not a registered user.Please make an account first");
                            return;
                        }
                    };
                    Apperyio.navigateTo('TypeChoice', {
                        reverse: false
                    });
                    localStorage.setItem('page_from', 'UploadAd');

                }
            },
        }, '#Welcome_mobilefooter1 [name="upload_item"]');

    };

    $(document).off("pagebeforeshow", "#Welcome").on("pagebeforeshow", "#Welcome", function(event, ui) {
        Apperyio.CurrentScreen = "Welcome";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    Welcome_onLoad();
};

$(document).off("pagecreate", "#Welcome").on("pagecreate", "#Welcome", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Welcome_js();
});